package lonja;

public class Carga {

	private int kilosVieira;
	private int kilosCentollo;
	private int kilosPulpo;

	public Carga(int kilosVieira, int kilosCentollo, int kilosPulpo) {
		this.kilosVieira = kilosVieira;
		this.kilosCentollo = kilosCentollo;
		this.kilosPulpo = kilosPulpo;
	}
	
	public int getKilosVieiras() {
		// TODO Auto-generated method stub
		return kilosVieira;
	}

	public int getKilosCentollo() {
		// TODO Auto-generated method stub
		return kilosCentollo;
	}

	public int getKilosPulpo() {
		// TODO Auto-generated method stub
		return kilosPulpo;
	}

}
